import Foundation

final class MovieDetailPresenter {
    
    // MARK: - Private properties -

    private unowned let view: MovieDetailViewInterface
    private let interactor: MovieDetailInteractorInterface
    private let wireframe: MovieDetailWireframeInterface

    // MARK: - Lifecycle -

    init(view: MovieDetailViewInterface, interactor: MovieDetailInteractorInterface, wireframe: MovieDetailWireframeInterface) {
        self.view = view
        self.interactor = interactor
        self.wireframe = wireframe
    }
}

// MARK: - Extensions -

extension MovieDetailPresenter: MovieDetailPresenterInterface {
    func goToReview(with movieId: Int) {
        wireframe.routeToReview(with: movieId)
    }
    
    func getMovieTrailer(with movieId: Int) {
        interactor.fetchMovieTrailer(with: movieId)
    }
    
    func interactorDidFetchTrailer(with result: Result<[Trailer], Error>) {
        switch result {
        case .success(let trailers):
            view.update(with: trailers)
        case .failure:
            view.update(with: "Periksa kembali koneksi internet anda")
        }
    }
    
    func getMovieDetail(with movieId: Int) {
        interactor.fetchMovieDetail(with: movieId)
    }
    
    func interactorDidFetchMovie(with result: Result<Movie, Error>) {
        switch result {
        case .success(let movie):
            view.update(with: movie)
        case .failure:
            view.update(with: "Periksa kembali koneksi internet anda")
        }
    }
    
}
